import Image from "next/image";
import React from "react";

type ImagePropTypes = React.ComponentProps<typeof Image>;

type PictureProps = ImagePropTypes;

function Picture({ src, alt, ...rest }: PictureProps) {
  return (
    <Image
      {...rest}
      alt={alt}
      src={src ? src : "/placeholder.png"}
      placeholder="blur"
      blurDataURL="data:image/png;base64,"
    />
  );
}

export default Picture;
